package com.reactlibrary;

import androidx.annotation.NonNull;

import com.facebook.react.bridge.Promise;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;

public final class BabylonModule extends ReactContextBaseJavaModule {

    private final ReactApplicationContext reactContext;

    BabylonModule(ReactApplicationContext reactContext) {
        super(reactContext);
        this.reactContext = reactContext;
    }

    @NonNull
    @Override
    public String getName() {
        return "BabylonModule";
    }

    @ReactMethod
    public void initializeEngine(Promise promise) {
        BabylonNativeInterop.whenInitialized(this.reactContext).thenAccept(instanceRef -> promise.resolve(null));
    }
}
